<script>
  var formCounter = 0;
  function submitForm(event) {
    formCounter++;
    if (formCounter > 1) {
      event.preventDefault();
      return false;
    }
  }

  function onSubmit(token) {
    $("#Contact-us").submit();

    alert($("#Contact-us").parsley().isValid());
    //contactvalidateForm();
    if (!$("#Contact-us").parsley().isValid()) {
      alert("if");
      $("#Contact-us").parsley().validate();
      return false;
    }
    else {
      alert("else");
      $("#Contact-us").submit();
      return true;
    }
  }

</script>






